import './App.css';
import Navbar from './Navbar';
import Navigator from './Navigator';
import Home from './Home';
import Projects from './Projects';
import Contact from './Contact';
import { useState, createContext } from "react";
import { Reveal } from "react-awesome-reveal";

const fadeStationary = keyframes`
  from {
    opacity: 0;
    transform: translate3d(-200px, -100px, 0);
  }

  to {
    opacity: 1;
    transform: translate3d(0, 0, 0);
  }
`;

function FadeStationary({ children }) {
  return <Reveal triggerOnce = "true" keyframes={fadeStationary}>{children}</Reveal>;
}

export const NavigatingContext = createContext()

function App() {
const [navigating, setNavigating] = useState("notNavigating")

  return (
    <NavigatingContext.Provider value={[navigating, setNavigating]}>
      <div className={"App " + navigating}>
        <Navbar/>
        <Navigator/>
        <Home/>
        <Slide triggerOnce="true">
          <hr/>
        </Slide>

        <Projects/>
        <hr/>
        <Contact/>
      </div>

    </NavigatingContext.Provider>

  );
}

export default App;
